eurekastreams.container.generateSecureToken = function(ownerId, viewerId, appId, url) {
    var fields = [ownerId, viewerId, appId, "shindig", url, "0", "default"];
    for (var i = 0; i < fields.length; i++) {
        // escape each field individually, for metachars in URL
        fields[i] = escape(fields[i]);
    }
    return fields.join(":");
};

/**
 * This needs to support gadget token use.  Currently, requests only work when security
 * token is overriden.  Also, this always sets the content type to JSON.
 */
eurekastreams.container.sendRequestToServer = function(
        url, method, opt_postParams, opt_callback, opt_excludeSecurityToken, opt_contentType) {
    // TODO: Should re-use the jsoncontainer code somehow
    opt_postParams = opt_postParams || {};

    var makeRequestParams = {
      "CONTENT_TYPE" : "JSON",
      "METHOD" : method,
      "POST_DATA" : opt_postParams};

    if (!opt_excludeSecurityToken) {
      url = socialDataPath + url + "?st=" + gadget.secureToken;
    }

    gadgets.io.makeNonProxiedRequest(url,
      function(data) {
        data = data.data;
        if (opt_callback) {
            opt_callback(data);
        }
      },
      makeRequestParams, 
      opt_contentType ? opt_contentType : "application/json"
    );
  };

eurekastreams.container.getGadgetArrayJSON = function()
{
    var gadgetsJSON = '[';
    for(var gadgetKey in gadgets.container.getGadgets())
    {
        gadgetsJSON += '{"url": "' + gadgets.container.getGadgets()[gadgetKey].specUrl + '"' + 
            ', "moduleId": "' + gadgets.container.getGadgets()[gadgetKey].appId + '"},';
    }
    gadgetsJSON = gadgetsJSON.substr(0, gadgetsJSON.length-1);
    gadgetsJSON += ']';
    return gadgetsJSON;
}
eurekastreams.container.requestGadgetMetaData = function(opt_callback) {
    var request = '{';
    request += '"context": {'; 
    request += '"country": "default",'; 
    request += '"language": "default",';
    request += '"view": "' + gadgets.container.getView() + '",';
    request += '"container": "eureka"';
    request += '},"gadgets": ';
    request += eurekastreams.container.getGadgetArrayJSON() + '}';

    eurekastreams.container.sendRequestToServer("/gadgets/metadata", "POST",
        request, opt_callback, true);
};

